.proc	_load_room: near

.segment	"CODE"

;
; clear_vram_buffer();
;
	jsr     _clear_vram_buffer
;
; offset = Level_offsets[level];
;
	ldy     _level
	lda     _Level_offsets,y
	sta     _offset
;
; set_data_pointer(Levels_list[offset]);
;
	ldx     #$00
	lda     _offset
	asl     a
	bcc     L1F86
	inx
	clc
