; ---------------------------------------------------------------
; void __near__ load_title (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_load_title: near

.segment	"CODE"

;
; pal_bg(palette_title);
;
	lda     #<(_palette_title)
	ldx     #>(_palette_title)
	jsr     _pal_bg
;
; pal_spr(palette_sp);
;
	lda     #<(_palette_sp)
	ldx     #>(_palette_sp)
	jsr     _pal_spr
;
; vram_adr(NAMETABLE_A);
;
	ldx     #$20
	lda     #$00
	jsr     _vram_adr
;
; vram_unrle(title);
;
	lda     #<(_title)
	ldx     #>(_title)
	jsr     _vram_unrle
;
; game_mode = MODE_TITLE;
;
	lda     #$00
	sta     _game_mode
;
; }
;
	rts

.endproc

